CREATE DATABASE IF NOT EXISTS zgcbd;

CREATE TABLE `pack` (
  `dpid` int NOT NULL,
  `timestamp` timestamp(6) NULL DEFAULT NULL,
  `seq` bigint DEFAULT NULL,
  `ack` bigint DEFAULT NULL,
  `ipv4_src` varchar(8) DEFAULT NULL,
  `ipv4_dst` varchar(8) DEFAULT NULL,
  `eth_src` varchar(17) DEFAULT NULL,
  `eth_dst` varchar(17) DEFAULT NULL,
  `ethertype` varchar(5) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;


-- zgcbd.station definition

CREATE TABLE `station` (
  `dpid` int NOT NULL,
  `longitude` double DEFAULT NULL,
  `latitude` double DEFAULT NULL,
  `label` varchar(5) DEFAULT NULL,
  PRIMARY KEY (`dpid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;



